mapper.select:
SELECT id,



FROM ppl_people as it
WHERE it.id = #{id}

SELECT id,



FROM ppl_people as it
WHERE it.id in (null

  #{item}

)
ORDER BY it.id desc

SELECT id,



FROM ppl_people as it
WHERE 1>0

    AND it.is_active = 1


    AND it.is_active = 0


    AND it.id >= #{idMin}


    AND it.id < #{idMax}


    AND it.creation_date >= #{creationDateMin}


    AND it.creation_date < #{creationDateMax}


    AND it.last_updated >= #{lastUpdatedMin}


    AND it.last_updated < #{lastUpdatedMax}

ORDER BY it.id desc
LIMIT ${pageOffset},${pageSize}

SELECT count(*)
FROM ppl_people as it
WHERE 1>0

    AND it.is_active = 1


    AND it.is_active = 0


    AND it.id >= #{idMin}


    AND it.id < #{idMax}


    AND it.creation_date >= #{creationDateMin}


    AND it.creation_date < #{creationDateMax}


    AND it.last_updated >= #{lastUpdatedMin}


    AND it.last_updated < #{lastUpdatedMax}

SELECT id,



FROM ppl_people as it
WHERE 1>0

    AND it.is_active = 1


    AND it.is_active = 0


    AND it.id >= #{idMin}


    AND it.id < #{idMax}


    AND it.creation_date >= #{creationDateMin}


    AND it.creation_date < #{creationDateMax}


    AND it.last_updated >= #{lastUpdatedMin}


    AND it.last_updated < #{lastUpdatedMax}

  and (it.nickname regexp #{namePattern}
    or it.first_name regexp #{namePattern}
    or it.last_name regexp #{namePattern}
  )
ORDER BY it.id desc
LIMIT ${pageOffset},${pageSize}
